generated.css:
body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen,
    Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
}

button {
  all: unset;
  background-color: steelblue;
  color: white;
  padding: 5px 20px;
  border-radius: 5px;
  cursor: pointer;
}

.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.modal__overlay {
  background-color: rgba(0, 0, 0, 0.6);
  width: 100%;
  height: 100%;
  position: absolute;
}

.modal__content {
  padding: 50px 100px;
  text-align: center;
  background-color: white;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
  position: relative;
  border-radius: 10px;
  width: 10%;
  top: 0;
  animation: 1s modalhidden ease-in-out;
}

h1 {
  margin: 0;
}

.hidden {
  display: none;
}

@keyframes modalhidden {
  0% {
    transform: none;
    margin-left: 100%;
  }
  30% {
    margin-left: 70%;
    transform: rotate(90deg) scale(1.5);
  }
  60% {
    margin-left: 30%;
    transform: rotate(270deg) scale(0.7);
  }
  100% {
    margin-left: 0;
    transform: rotate(360deg);
  }
}
